import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

function App() {
  const [file, setFile] = useState(null);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false); // State to manage the visibility of the success message
  const [excelData, setExcelData] = useState(null); // State to store the data from the uploaded Excel file

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    try {
      if (!file) {
        console.error('Please select a file');
        return;
      }

      const formData = new FormData();
      formData.append('excelFile', file);

      // Correct the URL to use the correct endpoint
      console.log('confirming with server!');
      const response = await axios.post('/api/upload-excel', formData);
      console.log('File uploaded successfully!');
      setShowSuccessMessage(true); // Set showSuccessMessage to true after successful upload
      setExcelData(response.data); // Set the data received from the server response
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div className="container">
      <header className="header">
        <div className="row">
          <div className="col">
            <img src="../favicon.ico" alt="Logo" />
          </div>
          <div className="col">
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <ul className="navbar-nav mr-auto">
                <li className="nav-item active">
                  {/* Use Link component to navigate to the dynamic form */}
                  <Link className="nav-link" to="/analysis">Analyse</Link>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      </header>
      <div className="card">
        <div className="card-body">
          <h2 className="card-title">Excel Upload</h2>
          <input className="form-control" type="file" accept=".xlsx, .xls" onChange={handleFileChange} />
          <button className="btn btn-primary mt-3" onClick={handleUpload}>Upload</button>
          {/* Display success message if showSuccessMessage is true */}
          {showSuccessMessage && (
            <div className="alert alert-success mt-3" role="alert">
              File uploaded successfully!
            </div>
          )}
        </div>
      </div>
      {/* Display the Excel data if available */}
      {excelData && excelData.headers && excelData.rows && (
        <div className="card mt-3">
          <div className="card-body">
            <h2 className="card-title">Preview of Uploaded Excel</h2>
            {/* Render the Excel data in a table */}
            <table className="table table-bordered">
              <thead>
                <tr>
                  {excelData.headers.map(header => (
                    <th key={header}>{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {excelData.rows.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {row.map((cell, cellIndex) => (
                      <td key={cellIndex}>{cell}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
