import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

function App() {
  const [file, setFile] = useState(null);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false); // State to manage the visibility of the success message

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    try {
      if (!file) {
        console.error('Please select a file');
        return;
      }

      const formData = new FormData();
      formData.append('excelFile', file);

      // Correct the URL to use the correct endpoint
      console.log('confirming with server!');
      await axios.post('/api/upload-excel', formData);

      console.log('File uploaded successfully!');
      setShowSuccessMessage(true); // Set showSuccessMessage to true after successful upload
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div className="container">
      <header className="header">
        <div className="row">
          <div className="col">
            <img src="../favicon.ico" alt="Logo" />
          </div>
          <div className="col">
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <ul className="navbar-nav mr-auto">
                <li className="nav-item active">
                  {/* Use Link component to navigate to the dynamic form */}
                  <Link className="nav-link" to="/analysis">Analyse</Link>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      </header>
      <div className="excel-upload">
        <h2>Excel Upload</h2>
        <input className="form-control" type="file" accept=".xlsx, .xls" onChange={handleFileChange} />
        <button className="btn btn-primary mt-3" onClick={handleUpload}>Upload</button>
      </div>
      {/* Display success message if showSuccessMessage is true */}
      {showSuccessMessage && (
        <div className="alert alert-success mt-3" role="alert">
          File uploaded successfully!
        </div>
      )}
    </div>
  );
}

export default App;
