import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const DynamicForm = () => {
    const [selectedFilters, setSelectedFilters] = useState({});
    const history = useHistory(); // Use useHistory hook to navigate

    const options = {
        Status: ['Active', 'Serving Notice', 'Resigned'],
        Type: ['Permanent', 'Probation', 'Contract'],
        'Notice Period': ['30 Days', '45 Days', '60 Days', '90 Days'],
        'Work Location': ['CSEZ', 'Home', 'Onsite', 'Eazylearn'],
        'Primary Skill': ['ASP.Net', 'PHP', 'SQL Server', 'MERN', 'MEAN', 'Fullstack', 'AI', 'Data Science', 'Java', 'React JS', 'React Native', 'Excel', 'IIB', 'UI', 'Project Office'],
        Gender: ['Male', 'Female', 'Others'],
        'Marital Status': ['Single', 'Married', 'Widow', 'Widower', 'Divorced', 'Separated'],
        'Bloog Group': ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-', 'K', 'Not Known'],
        'Work Timing': ['9:30 AM - 6:30 PM', '4:00 AM - 1:00 PM', '4:00 PM - 1:00 AM'],
        Flexibility: ['30 Minutes', '45 Minutes'],
        Weekday: ['MON', 'TUE', 'WED', 'THU', 'FRI', 'SAT', 'SUN']
    };

    const handleFilterChange = (fieldName, value) => {
        // Update selected filters when the user selects a filter
        setSelectedFilters(prevFilters => ({
            ...prevFilters,
            [fieldName]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        // Handle form submission and send selected filters to the backend
        axios.post('/api/submit-filters', selectedFilters)
            .then(response => {
                console.log('Filters saved successfully:', response.data.message);
                // Redirect to another page after saving filters
                history.push('/results');
            })
            .catch(error => {
                console.error('Error saving filters:', error);
            });
    };

    return (
        <div className="container">
            <h2>Dynamic Form</h2>
            <form onSubmit={handleSubmit}>
                {/* Iterate over options and create dropdowns */}
                {Object.entries(options).map(([fieldName, values]) => (
                    <div key={fieldName} className="form-group">
                        <label htmlFor={fieldName}>{fieldName}</label>
                        <select
                            id={fieldName}
                            name={fieldName}
                            onChange={(e) => handleFilterChange(fieldName, e.target.value)}
                            className="form-control"
                        >
                            <option value="">Select {fieldName}...</option>
                            {/* Iterate over values and create options */}
                            {values.map((value, index) => (
                                <option key={index} value={value}>{value}</option>
                            ))}
                        </select>
                    </div>
                ))}
                <button type="submit" className="btn btn-primary">Save</button>
            </form>
        </div>
    );
};

export default DynamicForm;
