import React, { useState } from 'react';

const LoginPage = () => {
  const [credentials, setCredentials] = useState({ name: '', password: '' });
  const [errorMessage, setErrorMessage] = useState('');
  const [authenticated, setAuthenticated] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();

    const { name, password } = credentials;

    try {
      // Simulate authentication logic on the client side
      if (name === 'admin' && password === 'admin') {
        console.log('Login successful');
        setErrorMessage('');
        setAuthenticated(true);
      } else {
        console.error('Login failed');
        setErrorMessage('Invalid username or password');
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCredentials((prevCredentials) => ({ ...prevCredentials, [name]: value }));
  };

  // Redirect to the next page if authenticated
  if (authenticated) {
    // Replace the following line with the appropriate navigation logic
    return <p>Redirecting to the next page...</p>;
  }

  return (
    <section className="section section--login">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6 col-lg-4">
            <div className="card">
              <div className="card-body">
                <img src="../favico" alt="Logo" className="img-fluid mb-3" />
                <form onSubmit={handleLogin}>
                  <div className="form-group">
                    <label htmlFor="name">Username</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={credentials.name}
                      onChange={handleInputChange}
                      className="form-control"
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                      type="password"
                      id="password"
                      name="password"
                      value={credentials.password}
                      onChange={handleInputChange}
                      className="form-control"
                    />
                  </div>
                  <button type="submit" className="btn btn-primary btn-block">
                    Login
                  </button>
                  {errorMessage && <p className="error-message mt-3">{errorMessage}</p>}
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default LoginPage;
