import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DynamicForm = () => {
    const [columns, setColumns] = useState([]);
    const [selectedFilters, setSelectedFilters] = useState({});

    useEffect(() => {
        // Fetch list of columns from the backend
        axios.get('/api/dynamic-form')
            .then(response => {
                setColumns(response.data.columns);
            })
            .catch(error => {
                console.error('Error fetching columns:', error);
            });
    }, []);

    const handleFilterChange = (columnName, value) => {
        // Update selected filters when the user selects a filter
        setSelectedFilters(prevFilters => ({
            ...prevFilters,
            [columnName]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        // Handle form submission and send selected filters to the backend
        axios.post('/api/dynamic-form', selectedFilters)
            .then(response => {
                console.log('Filters saved successfully:', response.data.message);
            })
            .catch(error => {
                console.error('Error saving filters:', error);
            });
    };

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-8 offset-md-2">
                    <div className="dynamic-form">
                        <h2 className="text-center mb-4">Dynamic Form</h2>
                        <form onSubmit={handleSubmit}>
                            {columns.map(column => (
                                <div key={column.filterName} className="form-group row">
                                    <label htmlFor={column.filterName} className="col-sm-3 col-form-label">{column.filterName}</label>
                                    <div className="col-sm-9">
                                        <select id={column.filterName} name={column.filterName} className="form-control" onChange={(e) => handleFilterChange(column.filterName, e.target.value)}>
                                            <option value="">Select filter...</option>
                                            {/* Check if filterConditions is an array before mapping over it */}
                                            {Array.isArray(column.filterConditions) && column.filterConditions.map(option => (
                                                <option key={option} value={option}>{option}</option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                            ))}
                            <div className="text-center">
                                <button type="submit" className="btn btn-primary mt-4">Save</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DynamicForm;
