import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);
  const [excelData, setExcelData] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    try {
      if (!file) {
        console.error('Please select a file');
        return;
      }

      const formData = new FormData();
      formData.append('excelFile', file);

      const response = await axios.post('/api/upload-excel', formData);
      console.log('File uploaded successfully!');
      setShowSuccessMessage(true);
      // Check if response data exists and has expected structure
      if (response.data && Array.isArray(response.data.data)) {
        setExcelData(response.data.data); // Set the data received from the server response
      } else {
        console.error('Invalid response format:', response.data);
      }
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <div className="card-body">
          <h2 className="card-title">Excel Upload</h2>
          <input className="form-control" type="file" accept=".xlsx, .xls" onChange={handleFileChange} />
          <button className="btn btn-primary mt-3" onClick={handleUpload}>Upload</button>
          {showSuccessMessage && (
            <div className="alert alert-success mt-3" role="alert">
              File uploaded successfully!
            </div>
          )}
          {excelData && excelData.headers && excelData.rows && (
            <div className="mt-3">
              <h3>Preview of Uploaded Excel</h3>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    {excelData.headers.map((header, index) => (
                      <th key={index}>{header}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {excelData.rows.map((row, rowIndex) => (
                    <tr key={rowIndex}>
                      {row.map((cell, cellIndex) => (
                        <td key={cellIndex}>{cell}</td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
