import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const DynamicForm = () => {
    const [options, setOptions] = useState({
        status: ["Active", "Serving Notice", "Resigned"],
        type: ["Permanent", "Probation", "Contract"],
        noticePeriod: ["30 Days", "45 Days", "60 Days", "90 Days"],
        workLocation: ["CSEZ", "Home", "Onsite", "Eazylearn"],
        primarySkill: ["ASP.Net", "PHP", "SQL Server", "MERN", "MEAN", "Fullstack", "AI", "Data Science", "Java", "React JS", "React Native", "Excel", "IIB", "UI", "Project Office"],
        gender: ["Male", "Female", "Others"],
        maritalStatus: ["Single", "Married", "Widow", "Widower", "Divorced", "Separated"],
        bloodGroup: ["A+", "A-", "B+", "B-", "AB+", "AB-", "O+", "O-", "K", "Not Known"],
        workTiming: ["9:30 AM - 6:30 PM", "4:00 AM - 1:00 PM", "4:00 PM - 1:00 AM"],
        flexibility: ["30 Minutes", "45 Minutes"],
        weekday: ["MON", "TUE", "WED", "THU", "FRI", "SAT", "SUN"]
    });
    const [selectedFilters, setSelectedFilters] = useState({});
    const history = useHistory();

    useEffect(() => {
        // Fetch list of columns from the backend
        axios.get('/api/dynamic-form')
            .then(response => {
                setColumns(response.data.columns);
            })
            .catch(error => {
                console.error('Error fetching columns:', error);
            });
    }, []);

    const handleFilterChange = (columnName, value) => {
        // Update selected filters when the user selects a filter
        setSelectedFilters(prevFilters => ({
            ...prevFilters,
            [columnName]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        // Handle form submission and send selected filters to the backend
        axios.post('/api/dynamic-form', selectedFilters)
            .then(response => {
                console.log('Filters saved successfully:', response.data.message);
                // Redirect to another page
                history.push('/other-page');
            })
            .catch(error => {
                console.error('Error saving filters:', error);
            });
    };

    return (
        <div className="dynamic-form">
            <h2>Dynamic Form</h2>
            <form onSubmit={handleSubmit}>
                {Object.entries(options).map(([category, values]) => (
                    <div key={category} className="form-group">
                        <label htmlFor={category}>{category}</label>
                        <select id={category} name={category} onChange={(e) => handleFilterChange(category, e.target.value)}>
                            <option value="">Select {category}...</option>
                            {values.map(option => (
                                <option key={option} value={option}>{option}</option>
                            ))}
                        </select>
                    </div>
                ))}
                <button type="submit" className="btn btn-primary">Save</button>
            </form>
        </div>
    );
};

export default DynamicForm;
