import React, { useState } from 'react';
import axios from 'axios';

const DynamicForm = () => {
    const [selectedFilters, setSelectedFilters] = useState({});

    const options = {
        Status: ["Active", "Serving Notice", "Resigned"],
        Type: ["Permanent", "Probation", "Contract"],
        "Notice Period": ["30 Days", "45 Days", "60 Days", "90 Days"],
        "Work Location": ["CSEZ", "Home", "Onsite", "Eazylearn"],
        "Primary Skill": ["ASP.Net", "PHP", "SQL Server", "MERN", "MEAN", "Fullstack", "AI", "Data Science", "Java", "React JS", "React Native", "Excel", "IIB", "UI", "Project Office"],
        Gender: ["Male", "Female", "Others"],
        "Marital Status": ["Single", "Married", "Widow", "Widower", "Divorced", "Separated"],
        "Blood Group": ["A+", "A-", "B+", "B-", "AB+", "AB-", "O+", "O-", "K", "Not Known"],
        "Work Timing": ["9:30 AM - 6:30 PM", "4:00 AM - 1:00 PM", "4:00 PM - 1:00 AM"],
        Flexibility: ["30 Minutes", "45 Minutes"],
        Weekday: ["MON", "TUE", "WED", "THU", "FRI", "SAT", "SUN"]
    };

    const handleFilterChange = (columnName, value) => {
        setSelectedFilters(prevFilters => ({
            ...prevFilters,
            [columnName]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault(); 
        axios.post('/api/dynamic-form', selectedFilters)
            .then(response => {
                console.log('Filters saved successfully:', response.data.message);
            })
            .catch(error => {
                console.error('Error saving filters:', error);
            });
    };

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <div className="dynamic-form">
                        <h2 className="text-center">Dynamic Form</h2>
                        <form onSubmit={handleSubmit}>
                            {Object.entries(options).map(([fieldName, fieldOptions]) => (
                                <div key={fieldName} className="form-group">
                                    <label htmlFor={fieldName}>{fieldName}</label>
                                    <select id={fieldName} name={fieldName} className="form-control" onChange={(e) => handleFilterChange(fieldName, e.target.value)}>
                                        <option value="">Select filter...</option>
                                        {fieldOptions.map((option, index) => (
                                            <option key={index} value={option}>{option}</option>
                                        ))}
                                    </select>
                                </div>
                            ))}
                            <div className="text-center">
                                <button type="submit" className="btn btn-primary">Save</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DynamicForm;
